var Helpers = require('./helpers'),
    faker = require('../index');

var commerce = {
    product: function(){
        return[Helpers.randomize(faker.definitions.commerce.product_name.adjective),
            Helpers.randomize(faker.definitions.commerce.color),
            Helpers.randomize(faker.definitions.commerce.product_name.material),
            Helpers.randomize(faker.definitions.commerce.product_name.product)].join(' ')
    },
    department:function() {
        return Helpers.randomize(faker.definitions.commerce.department);
    }
}

module.exports = commerce;

